from z3 import *

# DECLARATIONS
islands = ['J', 'K', 'L', 'M', 'O']
num_islands = len(islands)
index = {islands[i]: i for i in range(num_islands)}

# Create variables for each possible bridge: 1 if bridge exists, 0 otherwise
# Since bridges are undirected, only consider pairs with i < j
bridge = {}
for i in range(num_islands):
    for j in range(i+1, num_islands):
        bridge[(i, j)] = Int(f"b_{islands[i]}{islands[j]}")

# CONSTRAINTS
solver = Solver()

# Each bridge variable is 0 or 1
for key in bridge:
    solver.add(Or(bridge[key] == 0, bridge[key] == 1))

# No island has more than 3 bridges
for i in range(num_islands):
    connected_bridges = []
    for j in range(num_islands):
        if i < j:
            connected_bridges.append(bridge[(i, j)])
        elif j < i:
            connected_bridges.append(bridge[(j, i)])
    solver.add(Sum(connected_bridges) <= 3)

# J, K, L each directly connected by bridge with one or both of M and O
# i.e. For each of J, K, L: connected to M or O or both
for island in ['J', 'K', 'L']:
    i = index[island]
    m = index['M']
    o = index['O']
    # bridge between i and m
    if i < m:
        b_im = bridge[(i, m)]
    else:
        b_im = bridge[(m, i)]
    # bridge between i and o
    if i < o:
        b_io = bridge[(i, o)]
    else:
        b_io = bridge[(o, i)]
    # at least one of these bridges exists
    solver.add(Or(b_im == 1, b_io == 1))

# J is directly connected by bridge with exactly two islands
j = index['J']
j_bridges = []
for k_ in range(num_islands):
    if j < k_:
        j_bridges.append(bridge[(j, k_)])
    elif k_ < j:
        j_bridges.append(bridge[(k_, j)])
solver.add(Sum(j_bridges) == 2)

# K is directly connected by bridge with exactly one island
k = index['K']
k_bridges = []
for k_ in range(num_islands):
    if k < k_:
        k_bridges.append(bridge[(k, k_)])
    elif k_ < k:
        k_bridges.append(bridge[(k_, k)])
solver.add(Sum(k_bridges) == 1)

# A bridge directly connects J with O
j = index['J']
o = index['O']
if j < o:
    solver.add(bridge[(j, o)] == 1)
else:
    solver.add(bridge[(o, j)] == 1)

# A bridge directly connects M with O
m = index['M']
o = index['O']
if m < o:
    solver.add(bridge[(m, o)] == 1)
else:
    solver.add(bridge[(o, m)] == 1)

# No more than one bridge directly connects any two islands (already modeled by bridge variables being 0 or 1)

# No two bridges intersect - this is a geometric constraint and is not explicitly modeled here,
# but since the problem is LSAT style and only one answer is correct, we rely on logical constraints.

# OPTION CHECKING

options = {}

# (A) J is directly connected by bridge both with L and with M.
# That means bridges J-L and J-M exist
j = index['J']
l = index['L']
m = index['M']
if j < l:
    b_jl = bridge[(j, l)]
else:
    b_jl = bridge[(l, j)]
if j < m:
    b_jm = bridge[(j, m)]
else:
    b_jm = bridge[(m, j)]
options['A'] = And(b_jl == 1, b_jm == 1)

# (B) K is directly connected by bridge both with M and with O.
k = index['K']
m = index['M']
o = index['O']
if k < m:
    b_km = bridge[(k, m)]
else:
    b_km = bridge[(m, k)]
if k < o:
    b_ko = bridge[(k, o)]
else:
    b_ko = bridge[(o, k)]
options['B'] = And(b_km == 1, b_ko == 1)

# (C) L is directly connected by bridge both with J and with M.
l = index['L']
j = index['J']
m = index['M']
if l < j:
    b_lj = bridge[(l, j)]
else:
    b_lj = bridge[(j, l)]
if l < m:
    b_lm = bridge[(l, m)]
else:
    b_lm = bridge[(m, l)]
options['C'] = And(b_lj == 1, b_lm == 1)

# (D) M is directly connected by bridge with J, with K, and with L.
m = index['M']
j = index['J']
k = index['K']
l = index['L']
if m < j:
    b_mj = bridge[(m, j)]
else:
    b_mj = bridge[(j, m)]
if m < k:
    b_mk = bridge[(m, k)]
else:
    b_mk = bridge[(k, m)]
if m < l:
    b_ml = bridge[(m, l)]
else:
    b_ml = bridge[(l, m)]
options['D'] = And(b_mj == 1, b_mk == 1, b_ml == 1)

# (E) O is directly connected by bridge with K, with L, and with M.
o = index['O']
k = index['K']
l = index['L']
m = index['M']
if o < k:
    b_ok = bridge[(o, k)]
else:
    b_ok = bridge[(k, o)]
if o < l:
    b_ol = bridge[(o, l)]
else:
    b_ol = bridge[(l, o)]
if o < m:
    b_om = bridge[(o, m)]
else:
    b_om = bridge[(m, o)]
options['E'] = And(b_ok == 1, b_ol == 1, b_om == 1)

# Check each option
for letter in ['A', 'B', 'C', 'D', 'E']:
    solver.push()
    solver.add(options[letter])
    if solver.check() == sat:
        print(f"'{letter}'")
        break
    solver.pop()
